const HotabAccordion = document.querySelectorAll('.accordion-card');
HotabAccordion.forEach((item, index) => {
    let AccordionHeader = item.querySelector('.accordion-card__header');    
    AccordionHeader.addEventListener("click", () => {
        item.classList.toggle('accordion-card--active');
        let AccordionContent = item.querySelector('.accordion-card__content');
        if(item.classList.contains('accordion-card--active')){
            setTimeout(() => {
                AccordionContent.style.opacity = "1"; 
            }, 100)
            AccordionContent.style.height = "100%";
        }else{
            setTimeout(() => { 
                AccordionContent.style.height = "0px";
            }, 1)
            AccordionContent.style.opacity = "0";
        }
        removeOpen(index);
    });
});
function removeOpen(index1){
    HotabAccordion.forEach((item2, index2) => {
        if(index1 != index2){
            item2.classList.remove("accordion-card--active");
            let des = item2.querySelector(".accordion-card__content");
            setTimeout(() => { 
                des.style.height = "0px";
            }, 1)
            des.style.opacity = "0";
        }
    })
}